function beforeClick(treeId, treeNode) {
  var zTree = $.fn.zTree.getZTreeObj("treeDemo");
  zTree.checkNode(treeNode, !treeNode.checked, null, true);
  return false;
}

function onCheck(e, treeId, treeNode) {
  var zTree = $.fn.zTree.getZTreeObj("treeDemo"),
    nodes = zTree.getCheckedNodes(true),
    v = "";
  for (var i=0, l=nodes.length; i<l; i++) {
    v += nodes[i].name + ",";
  }
  if (v.length > 0 ) v = v.substring(0, v.length-1);
  var cityObj = $("#categoryies-filter");
  cityObj.attr("value", v);
}

function showMenu() {
  var cityObj = $("#categoryies-filter");
  var cityOffset = $("#categoryies-filter").offset();
  $("#listCategoryFilter").css({left:cityOffset.left + "px", top:cityOffset.top + $("#categoryies-filter").height() + 15 + "px"}).slideDown("fast");

  $("body").bind("mousedown", onBodyDown);
}
function hideMenu() {
  $("#listCategoryFilter").fadeOut("fast");
  $("body").unbind("mousedown", onBodyDown);
}
function onBodyDown(event) {
  if (!(event.target.id == "menuBtn" || event.target.id == "categoryies-filter" || event.target.id == "listCategoryFilter" || $(event.target).parents("#listCategoryFilter").length>0)) {
    hideMenu();
  }
}

